import osimport csvfile_input = os.path.join("Resources", "budget_data.csv" )output_file = os.path.join("Analysis", "Final_Budget_Data.txt")total_months = 0month_of_change = []net_change_list = []greatest_increase = ["",0]greatest_decrease = ["", 99999999999]total_net = 0with open(file_input) as financial_data:     csvreader = csv.reader(financial_data, delimiter=",")    header = next(csvreader)        total_months += 1    first_row= next(csvreader)    total_net += int(first_row[1])    previous_net = int(first_row[1])         for row in csvreader:                #track the total         total_months += 1        total_net += int(row[1])                #track the net change        net_change = int(row[1]) - previous_net        net_change_list.append(net_change)        month_of_change.append(row[0])                #Calculate the greatest increase         if (net_change > greatest_increase[1]):            greatest_increase[0] = row[0]            greatest_increase[1] = net_change                #Calculate the greatest decrease        if (net_change < greatest_decrease[1]):            greatest_decrease[0] = row[0]            greatest_decrease[1] = net_change                #Calculate the average net change         net_average = round(int(net_change) / len(net_change_list), 2)            #generate output summary    output_summary = (f'Total Months= {total_months}\n'              f'Total= ${total_net}\n'              f'Average Change= ${net_average}\n'              f'Greatest Increase in Profits: {greatest_increase[0]} ${greatest_increase[1]}\n'              f'Greatest Decrease in Profits: {greatest_decrease[0]} ${greatest_decrease[1]}\n')    print(output_summary)            #export the results to text file    with open(output_file, "w") as txt_file:        txt_file.write(output_summary)                