import osimport csvfile_input = os.path.join("Resources", "election_data.csv")output_file = os.path.join("Analysis", "Final_Election_Data.txt")#total vote countertotal_votes = 0#Candidate Options and Vote Counterscandidate_options = []candidate_votes = {}#Winning Candidate and Winning Count Trackerwinning_candidate = ""winning_count = 0with open(file_input) as polling_data:    csvreader = csv.reader(polling_data, delimiter=",")    header = next(csvreader)        for row in csvreader:                #run the loader animation         print(". ", end=""),                #add to the total vote count         total_votes = total_votes + 1                #extract the candidate name for each row        candidate_name = row[2]                #if the candidate does not match any existing candidate...        #(in a way, out loop is "discovering" candidates as it goes)        if candidate_name not in candidate_options:                         #add it to the list of candidates in the running             candidate_options.append(candidate_name)                        #and begin tracking that candidate's voter count             candidate_votes[candidate_name] = 0                    #then add a vote to that candidate's count         candidate_votes[candidate_name] = candidate_votes[candidate_name] +1              #print the results and export the data to our text filewith open(output_file, "w") as txt_file:        #print the final vote count    output1 = (f'Total Votes= {total_votes}')    print(output1)            #save the final vote count to the text file     txt_file.write(output1)            #determine the winner by looping through the counts    for candidate in candidate_votes:                 #retrive vote count and percentage        votes = candidate_votes.get(candidate)        vote_percentage = round(int(votes) / int(total_votes) *100, 3)                #determine winning vote count and candidate         if (votes > winning_count):            winning_count = votes            winning_candidate = candidate                    #print each candidate's voter count and percentage         voter_info = (f'{candidate}: {vote_percentage}% ({votes})\n')        print(voter_info)                    #save each candidate's voter count and percentage to text file         txt_file.write(voter_info)                #print the winning candidate     winning_candidate = (f'Winner: {winning_candidate}\n')    print(winning_candidate)            #save the winning candidate's name to the text file    txt_file.write(winning_candidate)            